# Claude配置持久化管理脚本 (PowerShell版本)
# 用于快速加载和恢复Claude AI的rules、memories和MCP配置

param(
    [string]$Action = "menu"
)

# 设置脚本变量
$ScriptDir = Split-Path -Parent $MyInvocation.MyCommand.Path
$ClaudeDir = Join-Path $ScriptDir ".claude"
$BackupDir = Join-Path $ClaudeDir "backups"
$ConfigDir = $ClaudeDir

# 颜色函数
function Write-ColoredOutput {
    param(
        [string]$Message,
        [string]$Color = "White"
    )
    Write-Host $Message -ForegroundColor $Color
}

function Log-Info {
    param([string]$Message)
    Write-ColoredOutput "[INFO] $Message" "Cyan"
}

function Log-Success {
    param([string]$Message)
    Write-ColoredOutput "[SUCCESS] $Message" "Green"
}

function Log-Warning {
    param([string]$Message)
    Write-ColoredOutput "[WARNING] $Message" "Yellow"
}

function Log-Error {
    param([string]$Message)
    Write-ColoredOutput "[ERROR] $Message" "Red"
}

# 创建备份目录
function Create-BackupDir {
    if (-not (Test-Path $BackupDir)) {
        New-Item -ItemType Directory -Path $BackupDir -Force | Out-Null
        Log-Info "创建备份目录: $BackupDir"
    }
}

# 备份当前配置
function Backup-CurrentConfig {
    Log-Info "备份当前配置..."
    Create-BackupDir

    $Timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
    $BackupFile = Join-Path $BackupDir "claude-config-backup-$Timestamp.zip"

    $FilesToBackup = @()

    $ConfigFiles = @(
        "settings.local.json",
        "claude-rules.md", 
        "claude-memories.md",
        "mcp-config-status.md"
    )

    foreach ($File in $ConfigFiles) {
        $FilePath = Join-Path $ConfigDir $File
        if (Test-Path $FilePath) {
            $FilesToBackup += $FilePath
        }
    }

    if ($FilesToBackup.Count -gt 0) {
        try {
            Compress-Archive -Path $FilesToBackup -DestinationPath $BackupFile -Force
            Log-Success "配置已备份到: $BackupFile"
        }
        catch {
            Log-Error "备份失败: $($_.Exception.Message)"
        }
    }
    else {
        Log-Warning "未找到要备份的配置文件"
    }
}

# 验证配置文件
function Test-ConfigFiles {
    Log-Info "验证配置文件..."

    $Valid = $true

    # 检查rules文件
    $RulesPath = Join-Path $ConfigDir "claude-rules.md"
    if (-not (Test-Path $RulesPath)) {
        Log-Error "Rules配置文件不存在: claude-rules.md"
        $Valid = $false
    }
    else {
        Log-Success "Rules配置文件存在"
    }

    # 检查memories文件  
    $MemoriesPath = Join-Path $ConfigDir "claude-memories.md"
    if (-not (Test-Path $MemoriesPath)) {
        Log-Error "Memories配置文件不存在: claude-memories.md"
        $Valid = $false
    }
    else {
        Log-Success "Memories配置文件存在"
    }

    # 检查MCP配置
    $McpPath = Join-Path $ConfigDir "settings.local.json"
    if (-not (Test-Path $McpPath)) {
        Log-Error "MCP配置文件不存在: settings.local.json"
        $Valid = $false
    }
    else {
        # 验证JSON格式
        try {
            $JsonContent = Get-Content $McpPath -Raw | ConvertFrom-Json
            Log-Success "MCP配置文件格式正确"
        }
        catch {
            Log-Error "MCP配置文件JSON格式错误: $($_.Exception.Message)"
            $Valid = $false
        }
    }

    if ($Valid) {
        Log-Success "所有配置文件验证通过"
        return $true
    }
    else {
        Log-Error "配置文件验证失败"
        return $false
    }
}

# 检查MCP服务器状态
function Test-McpServers {
    Log-Info "检查MCP服务器配置状态..."

    $McpPath = Join-Path $ConfigDir "settings.local.json"
    if (Test-Path $McpPath) {
        try {
            $Config = Get-Content $McpPath -Raw | ConvertFrom-Json

            if ($Config.mcpServers) {
                $ServerCount = ($Config.mcpServers | Get-Member -MemberType NoteProperty).Count
                Log-Info "已配置MCP服务器数量: $ServerCount"

                # 列出已配置的服务器
                $Config.mcpServers | Get-Member -MemberType NoteProperty | ForEach-Object {
                    Log-Info "  - $($_.Name)"
                }

                # 检查权限配置
                if ($Config.permissions -and $Config.permissions.allow) {
                    $PermissionCount = $Config.permissions.allow.Count
                    Log-Info "已配置权限规则数量: $PermissionCount"
                }
            }
            else {
                Log-Warning "未找到MCP服务器配置"
            }
        }
        catch {
            Log-Error "解析MCP配置失败: $($_.Exception.Message)"
        }
    }
}

# 生成配置摘要
function New-ConfigSummary {
    Log-Info "生成配置摘要..."

    $SummaryFile = Join-Path $ConfigDir "config-summary.md"
    $Timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"

    $Summary = @"
# Claude AI 配置摘要

**生成时间**: $Timestamp

## 配置文件状态
"@

    # 检查各配置文件状态
    $ConfigFiles = @{
        "claude-rules.md" = "Rules配置"
        "claude-memories.md" = "Memories配置"  
        "settings.local.json" = "MCP配置"
    }

    foreach ($File in $ConfigFiles.Keys) {
        $FilePath = Join-Path $ConfigDir $File
        if (Test-Path $FilePath) {
            $Summary += "`n- ✅ $($ConfigFiles[$File]): $File"
        }
        else {
            $Summary += "`n- ❌ $($ConfigFiles[$File]): 缺失"
        }
    }

    $Summary += @"

## MCP服务器统计
"@

    # MCP服务器统计
    $McpPath = Join-Path $ConfigDir "settings.local.json"
    if (Test-Path $McpPath) {
        try {
            $Config = Get-Content $McpPath -Raw | ConvertFrom-Json

            if ($Config.mcpServers) {
                $ServerCount = ($Config.mcpServers | Get-Member -MemberType NoteProperty).Count
                $Summary += "`n- 已配置服务器数量: $ServerCount"
            }

            if ($Config.permissions -and $Config.permissions.allow) {
                $PermissionCount = $Config.permissions.allow.Count
                $Summary += "`n- 权限规则数量: $PermissionCount"
            }
        }
        catch {
            $Summary += "`n- MCP配置解析失败"
        }
    }

    $Summary += @"

## 使用说明
1. 重启Claude Code以使配置生效
2. 检查MCP服务器连接状态
3. 验证权限配置是否正确
4. 定期备份配置文件

## 配置文件位置
- Rules: `$ConfigDir\claude-rules.md`
- Memories: `$ConfigDir\claude-memories.md`
- MCP配置: `$ConfigDir\settings.local.json`
- 状态检查: `$ConfigDir\mcp-config-status.md`
"@

    $Summary | Out-File -FilePath $SummaryFile -Encoding UTF8
    Log-Success "配置摘要已生成: $SummaryFile"
}

# 显示菜单
function Show-Menu {
    Write-Host ""
    Write-Host "===================================" -ForegroundColor Magenta
    Write-Host "    Claude AI 配置管理工具" -ForegroundColor Magenta  
    Write-Host "===================================" -ForegroundColor Magenta
    Write-Host "1. 验证配置文件" -ForegroundColor White
    Write-Host "2. 备份当前配置" -ForegroundColor White
    Write-Host "3. 检查MCP服务器状态" -ForegroundColor White
    Write-Host "4. 生成配置摘要" -ForegroundColor White
    Write-Host "5. 全部执行" -ForegroundColor White
    Write-Host "6. 退出" -ForegroundColor White
    Write-Host "===================================" -ForegroundColor Magenta
}

# 主函数
function Start-ConfigManager {
    Log-Info "Claude AI 配置持久化管理工具启动"
    Log-Info "配置目录: $ConfigDir"

    if ($Action -ne "menu") {
        switch ($Action.ToLower()) {
            "validate" { Test-ConfigFiles }
            "backup" { Backup-CurrentConfig }
            "check" { Test-McpServers }
            "summary" { New-ConfigSummary }
            "all" {
                Log-Info "执行全部操作..."
                Backup-CurrentConfig
                Test-ConfigFiles
                Test-McpServers  
                New-ConfigSummary
                Log-Success "所有操作完成！"
            }
            default {
                Log-Error "未知操作: $Action"
                Log-Info "可用操作: validate, backup, check, summary, all"
            }
        }
        return
    }

    # 交互式菜单
    while ($true) {
        Show-Menu
        $Choice = Read-Host "请选择操作 (1-6)"

        switch ($Choice) {
            "1" { Test-ConfigFiles }
            "2" { Backup-CurrentConfig }
            "3" { Test-McpServers }
            "4" { New-ConfigSummary }
            "5" {
                Log-Info "执行全部操作..."
                Backup-CurrentConfig
                Test-ConfigFiles
                Test-McpServers
                New-ConfigSummary
                Log-Success "所有操作完成！"
            }
            "6" {
                Log-Info "退出程序"
                return
            }
            default {
                Log-Error "无效选择，请输入1-6"
            }
        }

        Write-Host ""
        Read-Host "按Enter键继续..."
    }
}

# 启动配置管理器
Start-ConfigManager

