        "settings.local.json" = "MCP配置"
    }

    foreach ($File in $ConfigFiles.Keys) {
        $FilePath = Join-Path $ConfigDir $File
        if (Test-Path $FilePath) {
            $Summary += "`n- ✅ $($ConfigFiles[$File]): $File"
        }
        else {
            $Summary += "`n- ❌ $($ConfigFiles[$File]): 缺失"
        }
    }

    $Summary += @"

## MCP服务器统计
"@

    # MCP服务器统计
    $McpPath = Join-Path $ConfigDir "settings.local.json"
    if (Test-Path $McpPath) {
        try {
            $Config = Get-Content $McpPath -Raw | ConvertFrom-Json

            if ($Config.mcpServers) {
                $ServerCount = ($Config.mcpServers | Get-Member -MemberType NoteProperty).Count
                $Summary += "`n- 已配置服务器数量: $ServerCount"
            }

            if ($Config.permissions -and $Config.permissions.allow) {
                $PermissionCount = $Config.permissions.allow.Count
                $Summary += "`n- 权限规则数量: $PermissionCount"
            }
        }
        catch {
            $Summary += "`n- MCP配置解析失败"
        }
    }

    $Summary += @"

## 使用说明
1. 重启Claude Code以使配置生效
2. 检查MCP服务器连接状态
3. 验证权限配置是否正确
4. 定期备份配置文件

## 配置文件位置
- Rules: `$ConfigDir\claude-rules.md`
- Memories: `$ConfigDir\claude-memories.md`
- MCP配置: `$ConfigDir\settings.local.json`
- 状态检查: `$ConfigDir\mcp-config-status.md`
"@

    $Summary | Out-File -FilePath $SummaryFile -Encoding UTF8
    Log-Success "配置摘要已生成: $SummaryFile"
}

# 显示菜单
function Show-Menu {
    Write-Host ""
    Write-Host "===================================" -ForegroundColor Magenta
    Write-Host "    Claude AI 配置管理工具" -ForegroundColor Magenta  
    Write-Host "===================================" -ForegroundColor Magenta
    Write-Host "1. 验证配置文件" -ForegroundColor White
    Write-Host "2. 备份当前配置" -ForegroundColor White
    Write-Host "3. 检查MCP服务器状态" -ForegroundColor White
    Write-Host "4. 生成配置摘要" -ForegroundColor White
    Write-Host "5. 全部执行" -ForegroundColor White
    Write-Host "6. 退出" -ForegroundColor White
    Write-Host "===================================" -ForegroundColor Magenta
}

# 主函数
function Start-ConfigManager {
    Log-Info "Claude AI 配置持久化管理工具启动"
    Log-Info "配置目录: $ConfigDir"

    if ($Action -ne "menu") {
        switch ($Action.ToLower()) {
            "validate" { Test-ConfigFiles }
            "backup" { Backup-CurrentConfig }
            "check" { Test-McpServers }
            "summary" { New-ConfigSummary }
            "all" {
                Log-Info "执行全部操作..."
                Backup-CurrentConfig
                Test-ConfigFiles
                Test-McpServers  
                New-ConfigSummary
                Log-Success "所有操作完成！"
            }
            default {
                Log-Error "未知操作: $Action"
                Log-Info "可用操作: validate, backup, check, summary, all"
            }
        }
        return
    }

    # 交互式菜单
    while ($true) {
        Show-Menu
        $Choice = Read-Host "请选择操作 (1-6)"

        switch ($Choice) {
            "1" { Test-ConfigFiles }
            "2" { Backup-CurrentConfig }
            "3" { Test-McpServers }
            "4" { New-ConfigSummary }
            "5" {
                Log-Info "执行全部操作..."
                Backup-CurrentConfig
                Test-ConfigFiles
                Test-McpServers
                New-ConfigSummary
                Log-Success "所有操作完成！"
            }
            "6" {
                Log-Info "退出程序"
                return
            }
            default {
                Log-Error "无效选择，请输入1-6"
            }
        }

        Write-Host ""
        Read-Host "按Enter键继续..."
    }
}

# 启动配置管理器
Start-ConfigManager

input.content:
# Claude Code 全局配置迁移脚本
# 将项目特定配置转换为全局配置，让所有项目都能使用这套配置

param(
    [string]$Action = "menu",
    [string]$SourcePath = ".\.claude",
    [string]$GlobalPath = "$env:USERPROFILE\.claude"
)

# 颜色函数
function Write-ColoredOutput {
    param([string]$Message, [string]$Color = "White")
    Write-Host $Message -ForegroundColor $Color
}

function Log-Info { param([string]$Message); Write-ColoredOutput "[INFO] $Message" "Cyan" }
function Log-Success { param([string]$Message); Write-ColoredOutput "[SUCCESS] $Message" "Green" }
function Log-Warning { param([string]$Message); Write-ColoredOutput "[WARNING] $Message" "Yellow" }
function Log-Error { param([string]$Message); Write-ColoredOutput "[ERROR] $Message" "Red" }

# 创建全局配置目录
function New-GlobalConfigDir {
    if (-not (Test-Path $GlobalPath)) {
        New-Item -ItemType Directory -Path $GlobalPath -Force | Out-Null
        Log-Info "创建全局配置目录: $GlobalPath"
    }

    # 创建子目录
    $SubDirs = @("backups", "templates", "rules", "memories")
    foreach ($Dir in $SubDirs) {
        $SubDirPath = Join-Path $GlobalPath $Dir
        if (-not (Test-Path $SubDirPath)) {
            New-Item -ItemType Directory -Path $SubDirPath -Force | Out-Null
        }
    }
}

# 备份现有全局配置
function Backup-GlobalConfig {
    Log-Info "备份现有全局配置..."

    if (Test-Path $GlobalPath) {
        $Timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
        $BackupPath = Join-Path $GlobalPath "backups\global-config-backup-$Timestamp.zip"

        $FilesToBackup = @()
        $ConfigFiles = @("settings.json", "settings.local.json")

        foreach ($File in $ConfigFiles) {
            $FilePath = Join-Path $GlobalPath $File
            if (Test-Path $FilePath) {
                $FilesToBackup += $FilePath
            }
        }

        if ($FilesToBackup.Count -gt 0) {
            try {
                Compress-Archive -Path $FilesToBackup -DestinationPath $BackupPath -Force
                Log-Success "全局配置已备份到: $BackupPath"
            }
            catch {
                Log-Warning "备份失败: $($_.Exception.Message)"
            }
        }
    }
}

# 迁移MCP配置到全局
function Move-McpConfigToGlobal {
    Log-Info "迁移MCP配置到全局..."

    $SourceConfigPath = Join-Path $SourcePath "settings.local.json"
    $GlobalConfigPath = Join-Path $GlobalPath "settings.local.json"

    if (-not (Test-Path $SourceConfigPath)) {
        Log-Error "源配置文件不存在: $SourceConfigPath"
        return $false
    }

    try {
        # 读取源配置
        $SourceConfig = Get-Content $SourceConfigPath -Raw | ConvertFrom-Json

        # 检查是否存在全局配置
        $GlobalConfig = @{}
        if (Test-Path $GlobalConfigPath) {
            $GlobalConfig = Get-Content $GlobalConfigPath -Raw | ConvertFrom-Json
        }

        # 合并MCP服务器配置
        if ($SourceConfig.mcpServers) {
            if (-not $GlobalConfig.mcpServers) {
                $GlobalConfig | Add-Member -NotePropertyName "mcpServers" -NotePropertyValue @{}
            }

            # 复制所有MCP服务器
            $SourceConfig.mcpServers | Get-Member -MemberType NoteProperty | ForEach-Object {
                $ServerName = $_.Name
                $ServerConfig = $SourceConfig.mcpServers.$ServerName
                $GlobalConfig.mcpServers | Add-Member -NotePropertyName $ServerName -NotePropertyValue $ServerConfig -Force
                Log-Info "已添加MCP服务器: $ServerName"
            }
        }

        # 合并权限配置
        if ($SourceConfig.permissions) {
            if (-not $GlobalConfig.permissions) {
                $GlobalConfig | Add-Member -NotePropertyName "permissions" -NotePropertyValue @{}
            }

            if ($SourceConfig.permissions.allow) {
                $GlobalConfig.permissions | Add-Member -NotePropertyName "allow" -NotePropertyValue $SourceConfig.permissions.allow -Force
                Log-Info "已迁移权限配置: $($SourceConfig.permissions.allow.Count) 条规则"
            }

            if ($SourceConfig.permissions.deny) {
                $GlobalConfig.permissions | Add-Member -NotePropertyName "deny" -NotePropertyValue $SourceConfig.permissions.deny -Force
            }
        }

        # 保存全局配置
        $GlobalConfig | ConvertTo-Json -Depth 10 | Out-File -FilePath $GlobalConfigPath -Encoding UTF8
        Log-Success "MCP配置已迁移到全局: $GlobalConfigPath"

        return $true
    }
    catch {
        Log-Error "迁移MCP配置失败: $($_.Exception.Message)"
        return $false
    }
}

# 迁移Rules和Memories到全局
function Move-RulesMemoriesToGlobal {
    Log-Info "迁移Rules和Memories到全局..."

    $FilesToMove = @{
        "claude-rules.md" = "rules\global-claude-rules.md"
        "claude-memories.md" = "memories\global-claude-memories.md"
        "mcp-config-status.md" = "mcp-config-status.md"
    }

    foreach ($File in $FilesToMove.Keys) {
        $SourceFile = Join-Path $SourcePath $File
        $TargetFile = Join-Path $GlobalPath $FilesToMove[$File]

        if (Test-Path $SourceFile) {
            $TargetDir = Split-Path $TargetFile -Parent
            if (-not (Test-Path $TargetDir)) {
                New-Item -ItemType Directory -Path $TargetDir -Force | Out-Null
            }

            Copy-Item $SourceFile $TargetFile -Force
            Log-Success "已迁移: $File -> $($FilesToMove[$File])"
        }
        else {
            Log-Warning "源文件不存在: $File"
        }
    }
}

# 创建项目配置模板
function New-ProjectTemplate {
    Log-Info "创建项目配置模板..."

    $TemplatePath = Join-Path $GlobalPath "templates"

    # 创建最小项目配置模板
    $MinimalConfig = @{
        "_comment" = "这是一个最小项目配置，继承全局配置"
        "extends" = "`$HOME/.claude/settings.local.json"
        "projectSpecific" = @{
            "name" = "项目名称"
            "description" = "项目描述"
        }
    } | ConvertTo-Json -Depth 5

    $MinimalTemplatePath = Join-Path $TemplatePath "minimal-project-settings.json"
    $MinimalConfig | Out-File -FilePath $MinimalTemplatePath -Encoding UTF8

    # 创建完整项目配置模板
    $FullTemplate = @{
        "_comment" = "完整项目配置模板，可覆盖全局配置"
        "extends" = "`$HOME/.claude/settings.local.json"
        "projectSpecific" = @{
            "name" = "项目名称"
            "description" = "项目描述"
            "customRules" = @()
            "customMemories" = @()
        }
        "mcpServers" = @{
            "_comment" = "项目特定的MCP服务器（可选）"
        }
        "permissions" = @{
            "allow" = @(
                "_comment: 项目特定权限（可选）"
            )
        }
    } | ConvertTo-Json -Depth 5

    $FullTemplatePath = Join-Path $TemplatePath "full-project-settings.json"
    $FullTemplate | Out-File -FilePath $FullTemplatePath -Encoding UTF8

    Log-Success "项目配置模板已创建:"
    Log-Success "  - 最小模板: $MinimalTemplatePath"
    Log-Success "  - 完整模板: $FullTemplatePath"
}

# 创建环境变量配置脚本
function New-EnvironmentSetup {
    Log-Info "创建环境变量配置脚本..."

    $EnvScriptPath = Join-Path $GlobalPath "setup-claude-env.ps1"

    $EnvScript = @'
# Claude Code 环境变量配置脚本
# 设置Claude Code的全局配置路径

param(
    [switch]$Permanent,
    [switch]$CurrentSession
)

$GlobalClaudePath = "$env:USERPROFILE\.claude"

function Set-ClaudeEnvironment {
    param([bool]$IsPermanent = $false)

    # 设置Claude配置目录环境变量
    $EnvVars = @{
        "CLAUDE_CONFIG_PATH" = $GlobalClaudePath
        "CLAUDE_GLOBAL_RULES" = "$GlobalClaudePath\rules\global-claude-rules.md"
        "CLAUDE_GLOBAL_MEMORIES" = "$GlobalClaudePath\memories\global-claude-memories.md"
        "CLAUDE_MCP_CONFIG" = "$GlobalClaudePath\settings.local.json"
    }

    foreach ($VarName in $EnvVars.Keys) {
        $VarValue = $EnvVars[$VarName]

        if ($IsPermanent) {
            # 设置用户级别的永久环境变量
            [Environment]::SetEnvironmentVariable($VarName, $VarValue, [EnvironmentVariableTarget]::User)
            Write-Host "已设置永久环境变量: $VarName = $VarValue" -ForegroundColor Green
        }
        else {
            # 设置当前会话的环境变量
            Set-Item -Path "env:$VarName" -Value $VarValue
            Write-Host "已设置会话环境变量: $VarName = $VarValue" -ForegroundColor Yellow
        }
    }
}

if ($Permanent) {
    Write-Host "设置永久环境变量..." -ForegroundColor Cyan
    Set-ClaudeEnvironment -IsPermanent $true
    Write-Host "请重启终端以使永久环境变量生效" -ForegroundColor Magenta
}
elseif ($CurrentSession) {
    Write-Host "设置当前会话环境变量..." -ForegroundColor Cyan
    Set-ClaudeEnvironment -IsPermanent $false
}
else {
    Write-Host "Claude Code 环境变量配置脚本"
    Write-Host "用法："
    Write-Host "  .\setup-claude-env.ps1 -Permanent      # 设置永久环境变量"
    Write-Host "  .\setup-claude-env.ps1 -CurrentSession # 设置当前会话环境变量"
}
'@

    $EnvScript | Out-File -FilePath $EnvScriptPath -Encoding UTF8
    Log-Success "环境变量配置脚本已创建: $EnvScriptPath"
}

# 创建配置同步脚本
function New-SyncScript {
    Log-Info "创建配置同步脚本..."

    $SyncScriptPath = Join-Path $GlobalPath "sync-config.ps1"

    $SyncScript = @'
# Claude Code 配置同步脚本
# 在全局配置和项目配置之间同步

param(
    [string]$Direction = "ToGlobal", # ToGlobal, ToProject, Bidirectional
    [string]$ProjectPath = ".",
    [switch]$Force
)

$GlobalPath = "$env:USERPROFILE\.claude"
$ProjectClaudePath = Join-Path $ProjectPath ".claude"

function Sync-ConfigToGlobal {
    param([string]$ProjectPath, [bool]$ForceOverwrite = $false)

    Write-Host "同步项目配置到全局..." -ForegroundColor Cyan

    $FilesToSync = @{
        "settings.local.json" = "settings.local.json"
        "claude-rules.md" = "rules\project-claude-rules.md"
        "claude-memories.md" = "memories\project-claude-memories.md"
    }

    foreach ($File in $FilesToSync.Keys) {
        $SourceFile = Join-Path $ProjectClaudePath $File
        $TargetFile = Join-Path $GlobalPath $FilesToSync[$File]

        if (Test-Path $SourceFile) {
            $TargetDir = Split-Path $TargetFile -Parent
            if (-not (Test-Path $TargetDir)) {
                New-Item -ItemType Directory -Path $TargetDir -Force | Out-Null
            }

            if (-not (Test-Path $TargetFile) -or $ForceOverwrite) {
                Copy-Item $SourceFile $TargetFile -Force
                Write-Host "已同步: $File" -ForegroundColor Green
            }
            else {
                Write-Host "目标文件已存在，跳过: $File (使用 -Force 强制覆盖)" -ForegroundColor Yellow
            }
        }
    }
}

function Sync-ConfigToProject {
    param([string]$ProjectPath, [bool]$ForceOverwrite = $false)

    Write-Host "同步全局配置到项目..." -ForegroundColor Cyan

    if (-not (Test-Path $ProjectClaudePath)) {
        New-Item -ItemType Directory -Path $ProjectClaudePath -Force | Out-Null
        Write-Host "已创建项目配置目录: $ProjectClaudePath" -ForegroundColor Green
    }

    $FilesToSync = @{
        "settings.local.json" = "settings.local.json"
        "rules\global-claude-rules.md" = "claude-rules.md"
        "memories\global-claude-memories.md" = "claude-memories.md"
    }

    foreach ($File in $FilesToSync.Keys) {
        $SourceFile = Join-Path $GlobalPath $File
        $TargetFile = Join-Path $ProjectClaudePath $FilesToSync[$File]

        if (Test-Path $SourceFile) {
            if (-not (Test-Path $TargetFile) -or $ForceOverwrite) {
                Copy-Item $SourceFile $TargetFile -Force
                Write-Host "已同步: $($FilesToSync[$File])" -ForegroundColor Green
            }
            else {
                Write-Host "目标文件已存在，跳过: $($FilesToSync[$File]) (使用 -Force 强制覆盖)" -ForegroundColor Yellow
            }
        }
    }
}

# 主同步逻辑
switch ($Direction) {
    "ToGlobal" {
        Sync-ConfigToGlobal -ProjectPath $ProjectPath -ForceOverwrite $Force
    }
    "ToProject" {
        Sync-ConfigToProject -ProjectPath $ProjectPath -ForceOverwrite $Force
    }
    "Bidirectional" {
        Write-Host "双向同步..." -ForegroundColor Magenta
        Sync-ConfigToGlobal -ProjectPath $ProjectPath -ForceOverwrite $Force
        Sync-ConfigToProject -ProjectPath $ProjectPath -ForceOverwrite $Force
    }
    default {
        Write-Host "未知同步方向: $Direction"
        Write-Host "可用选项: ToGlobal, ToProject, Bidirectional"
    }
}
'@

    $SyncScript | Out-File -FilePath $SyncScriptPath -Encoding UTF8
    Log-Success "配置同步脚本已创建: $SyncScriptPath"
}

# 显示菜单
function Show-Menu {
    Write-Host ""
    Write-Host "=========================================" -ForegroundColor Magenta
    Write-Host "    Claude Code 全局配置迁移工具" -ForegroundColor Magenta
    Write-Host "=========================================" -ForegroundColor Magenta
    Write-Host "1. 完整迁移到全局配置" -ForegroundColor White
    Write-Host "2. 仅迁移MCP服务器配置" -ForegroundColor White
    Write-Host "3. 仅迁移Rules和Memories" -ForegroundColor White
    Write-Host "4. 创建项目配置模板" -ForegroundColor White
    Write-Host "5. 设置环境变量" -ForegroundColor White
    Write-Host "6. 创建配置同步工具" -ForegroundColor White
    Write-Host "7. 查看当前配置状态" -ForegroundColor White
    Write-Host "8. 退出" -ForegroundColor White
    Write-Host "=========================================" -ForegroundColor Magenta
}

# 查看配置状态
function Show-ConfigStatus {
    Log-Info "当前配置状态:"
    Log-Info "源配置目录: $SourcePath"
    Log-Info "全局配置目录: $GlobalPath"

    Write-Host ""
    Write-Host "源配置文件:" -ForegroundColor Yellow
    $SourceFiles = @("settings.local.json", "claude-rules.md", "claude-memories.md")
    foreach ($File in $SourceFiles) {
        $FilePath = Join-Path $SourcePath $File
        if (Test-Path $FilePath) {
            Write-Host "  ✅ $File" -ForegroundColor Green
        }
        else {
            Write-Host "  ❌ $File" -ForegroundColor Red
        }
    }

    Write-Host ""
    Write-Host "全局配置文件:" -ForegroundColor Yellow
    if (Test-Path $GlobalPath) {
        $GlobalFiles = Get-ChildItem $GlobalPath -Recurse -File | ForEach-Object { $_.FullName.Replace("$GlobalPath\", "") }
        if ($GlobalFiles.Count -gt 0) {
            foreach ($File in $GlobalFiles) {
                Write-Host "  ✅ $File" -ForegroundColor Green
            }
        }
        else {
            Write-Host "  📁 全局配置目录存在但为空" -ForegroundColor Yellow
        }
    }
    else {
        Write-Host "  ❌ 全局配置目录不存在" -ForegroundColor Red
    }
}

# 主函数
function Start-GlobalMigration {
    Log-Info "Claude Code 全局配置迁移工具启动"
    Log-Info "源配置: $SourcePath"
    Log-Info "全局配置: $GlobalPath"

    if ($Action -ne "menu") {
        switch ($Action.ToLower()) {
            "full" {
                New-GlobalConfigDir
                Backup-GlobalConfig
                Move-McpConfigToGlobal
                Move-RulesMemoriesToGlobal
                New-ProjectTemplate
                New-EnvironmentSetup
                New-SyncScript
                Log-Success "完整迁移完成！"
            }
            "mcp" { Move-McpConfigToGlobal }
            "rules" { Move-RulesMemoriesToGlobal }
            "templates" { New-ProjectTemplate }
            "env" { New-EnvironmentSetup }
            "sync" { New-SyncScript }
            "status" { Show-ConfigStatus }
            default {
                Log-Error "未知操作: $Action"
                Log-Info "可用操作: full, mcp, rules, templates, env, sync, status"
            }
        }
        return
    }

    # 交互式菜单
    while ($true) {
        Show-Menu
        $Choice = Read-Host "请选择操作 (1-8)"

        switch ($Choice) {
            "1" {
                New-GlobalConfigDir
                Backup-GlobalConfig
                Move-McpConfigToGlobal
                Move-RulesMemoriesToGlobal
                New-ProjectTemplate
                New-EnvironmentSetup
                New-SyncScript
                Log-Success "完整迁移完成！"
            }
            "2" { Move-McpConfigToGlobal }
            "3" { Move-RulesMemoriesToGlobal }
            "4" { New-ProjectTemplate }
            "5" { New-EnvironmentSetup }
            "6" { New-SyncScript }
            "7" { Show-ConfigStatus }
            "8" {
                Log-Info "退出程序"
                return
            }
            default {
                Log-Error "无效选择，请输入1-8"
            }
        }

        Write-Host ""
        Read-Host "按Enter键继续..."
    }
}

# 启动迁移工具
Start-GlobalMigration

