        if ($FilesToBackup.Count -gt 0) {
            try {
                Compress-Archive -Path $FilesToBackup -DestinationPath $BackupPath -Force
                Log-Success "全局配置已备份到: $BackupPath"
            }
            catch {
                Log-Warning "备份失败: $($_.Exception.Message)"
            }
        }
    }
}

# 迁移MCP配置到全局
function Move-McpConfigToGlobal {
    Log-Info "迁移MCP配置到全局..."

    $SourceConfigPath = Join-Path $SourcePath "settings.local.json"
    $GlobalConfigPath = Join-Path $GlobalPath "settings.local.json"

    if (-not (Test-Path $SourceConfigPath)) {
        Log-Error "源配置文件不存在: $SourceConfigPath"
        return $false
    }

    try {
        # 读取源配置
        $SourceConfig = Get-Content $SourceConfigPath -Raw | ConvertFrom-Json

        # 检查是否存在全局配置
        $GlobalConfig = @{}
        if (Test-Path $GlobalConfigPath) {
            $GlobalConfig = Get-Content $GlobalConfigPath -Raw | ConvertFrom-Json
        }

        # 合并MCP服务器配置
        if ($SourceConfig.mcpServers) {
            if (-not $GlobalConfig.mcpServers) {
                $GlobalConfig | Add-Member -NotePropertyName "mcpServers" -NotePropertyValue @{}
            }

            # 复制所有MCP服务器
            $SourceConfig.mcpServers | Get-Member -MemberType NoteProperty | ForEach-Object {
                $ServerName = $_.Name
                $ServerConfig = $SourceConfig.mcpServers.$ServerName
                $GlobalConfig.mcpServers | Add-Member -NotePropertyName $ServerName -NotePropertyValue $ServerConfig -Force
                Log-Info "已添加MCP服务器: $ServerName"
            }
        }

        # 合并权限配置
        if ($SourceConfig.permissions) {
            if (-not $GlobalConfig.permissions) {
                $GlobalConfig | Add-Member -NotePropertyName "permissions" -NotePropertyValue @{}
            }

            if ($SourceConfig.permissions.allow) {
                $GlobalConfig.permissions | Add-Member -NotePropertyName "allow" -NotePropertyValue $SourceConfig.permissions.allow -Force
                Log-Info "已迁移权限配置: $($SourceConfig.permissions.allow.Count) 条规则"
            }

            if ($SourceConfig.permissions.deny) {
                $GlobalConfig.permissions | Add-Member -NotePropertyName "deny" -NotePropertyValue $SourceConfig.permissions.deny -Force
            }
        }

        # 保存全局配置
        $GlobalConfig | ConvertTo-Json -Depth 10 | Out-File -FilePath $GlobalConfigPath -Encoding UTF8
        Log-Success "MCP配置已迁移到全局: $GlobalConfigPath"

        return $true
    }
    catch {
        Log-Error "迁移MCP配置失败: $($_.Exception.Message)"
        return $false
    }
}

# 迁移Rules和Memories到全局
function Move-RulesMemoriesToGlobal {
    Log-Info "迁移Rules和Memories到全局..."

    $FilesToMove = @{
        "claude-rules.md" = "rules\global-claude-rules.md"
        "claude-memories.md" = "memories\global-claude-memories.md"
        "mcp-config-status.md" = "mcp-config-status.md"
    }

    foreach ($File in $FilesToMove.Keys) {
        $SourceFile = Join-Path $SourcePath $File
        $TargetFile = Join-Path $GlobalPath $FilesToMove[$File]

        if (Test-Path $SourceFile) {
            $TargetDir = Split-Path $TargetFile -Parent
            if (-not (Test-Path $TargetDir)) {
                New-Item -ItemType Directory -Path $TargetDir -Force | Out-Null
            }

            Copy-Item $SourceFile $TargetFile -Force
            Log-Success "已迁移: $File -> $($FilesToMove[$File])"
        }
        else {
            Log-Warning "源文件不存在: $File"
        }
    }
}

# 创建项目配置模板
function New-ProjectTemplate {
    Log-Info "创建项目配置模板..."

    $TemplatePath = Join-Path $GlobalPath "templates"

    # 创建最小项目配置模板
    $MinimalConfig = @{
        "_comment" = "这是一个最小项目配置，继承全局配置"
        "extends" = "`$HOME/.claude/settings.local.json"
        "projectSpecific" = @{
