package org.dromara.business.controller;

import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.util.stream.Collectors;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import jakarta.servlet.http.HttpServletResponse;
import jakarta.validation.constraints.*;
import cn.dev33.satoken.annotation.SaCheckPermission;
import org.springframework.web.bind.annotation.*;
import org.springframework.validation.annotation.Validated;
import org.dromara.common.idempotent.annotation.RepeatSubmit;
import org.dromara.common.log.annotation.Log;
import org.dromara.common.web.core.BaseController;
import org.dromara.common.mybatis.core.page.PageQuery;
import org.dromara.common.core.domain.R;
import org.dromara.common.core.validate.AddGroup;
import org.dromara.common.core.validate.EditGroup;
import org.dromara.common.log.enums.BusinessType;
import org.dromara.common.excel.utils.ExcelUtil;
import org.dromara.common.core.utils.MapstructUtils;
import com.baomidou.mybatisplus.core.conditions.query.LambdaQueryWrapper;
import com.baomidou.mybatisplus.core.toolkit.Wrappers;
import org.dromara.business.domain.Students;
import org.dromara.business.domain.vo.StudentsVo;
import org.dromara.business.domain.vo.StudentsWithPaymentVo;
import org.dromara.business.domain.vo.StudentStatusVo;
import org.dromara.business.domain.vo.StudentsImportVo;
import org.dromara.business.domain.bo.StudentsBo;
import org.dromara.business.domain.bo.StudentStatusBo;
import org.dromara.business.service.IStudentsService;
import org.dromara.business.api.service.IUsersApiService;
import org.dromara.business.api.vo.UserVo;
import org.dromara.common.mybatis.core.page.TableDataInfo;
import org.springframework.web.multipart.MultipartFile;
import org.dromara.common.satoken.utils.LoginHelper;
import org.dromara.common.core.exception.ServiceException;

/**
 * 学生档案
 *
 * @author Akari
 * @date 2025-06-13
 */
@Slf4j
@Validated
@RequiredArgsConstructor
@RestController
@RequestMapping("/models/students")
public class StudentsController extends BaseController {

    private final IStudentsService studentsService;

    /**
     * 查询学生档案列表
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/list")
    public TableDataInfo<StudentsVo> list(StudentsBo bo, PageQuery pageQuery) {
        return studentsService.queryPageList(bo, pageQuery);
    }

    /**
     * 查询学生档案列表（包含缴费状态）
     * 注意：此路由必须放在 /{id} 之前，避免路径冲突
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/listWithPayment")
    public TableDataInfo<StudentsWithPaymentVo> listWithPayment(StudentsBo bo, PageQuery pageQuery) {
        return studentsService.queryStudentsWithPaymentList(bo, pageQuery);
    }



    /**
     * 获取学生档案详细信息
     *
     * @param id 主键
     */
    @SaCheckPermission("models:students:query")
    @GetMapping("/{id}")
    public R<StudentsVo> getInfo(@NotNull(message = "主键不能为空")
                                     @PathVariable Long id) {
        return R.ok(studentsService.queryById(id));
    }

    /**
     * 导出学生档案列表（包含缴费状态）
     */
    @SaCheckPermission("models:students:export")
    @Log(title = "学生档案", businessType = BusinessType.EXPORT)
    @PostMapping("/export")
    public void export(StudentsBo bo, HttpServletResponse response) {
        try {
            log.info("开始导出学生档案，查询条件：{}", bo);

            // 使用不分页的查询方法，获取所有数据
            List<StudentsWithPaymentVo> list = studentsService.queryStudentsWithPaymentList(bo);

            if (list == null || list.isEmpty()) {
                log.warn("导出学生档案：查询结果为空");
                list = new ArrayList<>(); // 防止空指针异常
            }

            log.info("导出学生档案：共{}条记录", list.size());
            ExcelUtil.exportExcel(list, "学生档案", StudentsWithPaymentVo.class, response);
        } catch (Exception e) {
            log.error("导出学生档案失败", e);
            throw new ServiceException("导出失败：" + e.getMessage());
        }
    }

    /**
     * 新增学生档案
     */
    @SaCheckPermission("models:students:add")
    @Log(title = "学生档案", businessType = BusinessType.INSERT)
    @RepeatSubmit()
    // TODO: 临时注释敏感数据保护注解，待ruoyi-common-sensitive-protection模块修复后恢复
    /*
    @org.dromara.common.sensitive.protection.annotation.SensitiveDataProtection(
        types = {
            org.dromara.common.sensitive.protection.enums.SensitiveType.PHONE_NUMBER,
            org.dromara.common.sensitive.protection.enums.SensitiveType.REAL_NAME
        },
        strategy = org.dromara.common.sensitive.protection.enums.InterceptionStrategy.HASH_AND_BLOCK,
        complianceNote = "学员档案新增-个人信息保护法合规",
        businessScene = "student_registration",
        retentionDays = 30
    )
    */
    @PostMapping()
    public R<Void> add(@Validated(AddGroup.class) @RequestBody StudentsBo bo) {
        return toAjax(studentsService.insertByBo(bo));
    }

    /**
     * 修改学生档案
     */
    @SaCheckPermission("models:students:edit")
    @Log(title = "学生档案", businessType = BusinessType.UPDATE)
    @RepeatSubmit()
    @PutMapping()
    public R<Void> edit(@Validated(EditGroup.class) @RequestBody StudentsBo bo) {
        return toAjax(studentsService.updateByBo(bo));
    }

    /**
     * 删除学生档案
     *
     * @param ids 主键串
     */
    @SaCheckPermission("models:students:remove")
    @Log(title = "学生档案", businessType = BusinessType.DELETE)
    @DeleteMapping("/{ids}")
    public R<Void> remove(@NotEmpty(message = "主键不能为空")
                          @PathVariable Long[] ids) {
        return toAjax(studentsService.deleteWithValidByIds(List.of(ids), true));
    }

    // ========== 定制化业务API ==========

    /**
     * 查询待分配学生列表
     * 业务场景：查询尚未分配到任何在读班级的学生
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/unassigned")
    @Log(title = "待分配学生查询", businessType = BusinessType.OTHER)
    public TableDataInfo<StudentStatusVo> listUnassignedStudents(StudentStatusBo bo, PageQuery pageQuery) {
        return studentsService.queryUnassignedStudentList(bo, pageQuery);
    }

    /**
     * 查询已退学学生列表
     * 业务场景：查询已退学的学生，用于统计和跟进
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/withdrawn")
    @Log(title = "已退学学生查询", businessType = BusinessType.OTHER)
    public TableDataInfo<StudentStatusVo> listWithdrawnStudents(StudentStatusBo bo, PageQuery pageQuery) {
        return studentsService.queryWithdrawnStudentList(bo, pageQuery);
    }

    /**
     * 查询已毕业学生列表
     * 业务场景：查询已毕业的学生，用于成果展示和后续服务
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/graduated")
    @Log(title = "已毕业学生查询", businessType = BusinessType.OTHER)
    public TableDataInfo<StudentStatusVo> listGraduatedStudents(StudentStatusBo bo, PageQuery pageQuery) {
        return studentsService.queryGraduatedStudentList(bo, pageQuery);
    }

    /**
     * 查询在读学生列表
     * 业务场景：查询当前在读的学生，用于教学管理
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/active")
    @Log(title = "在读学生查询", businessType = BusinessType.OTHER)
    public TableDataInfo<StudentStatusVo> listActiveStudents(StudentStatusBo bo, PageQuery pageQuery) {
        return studentsService.queryActiveStudentList(bo, pageQuery);
    }

    /**
     * 查询学生状态详情
     * 业务场景：查看学生的完整状态信息，包括学习历程和统计数据
     *
     * @param id 学生ID
     */
    @SaCheckPermission("models:students:query")
    @GetMapping("/status/{id}")
    @Log(title = "学生状态详情查询", businessType = BusinessType.OTHER)
    public R<StudentStatusVo> getStudentStatusDetail(@NotNull(message = "学生ID不能为空")
                                                    @PathVariable Long id) {
        StudentStatusVo detail = studentsService.queryStudentStatusDetail(id);
        if (detail == null) {
            return R.fail("未找到该学生信息");
        }
        return R.ok(detail);
    }

    /**
     * 查询学生状态列表（通用方法）
     * 业务场景：根据复杂条件灵活查询不同状态的学生
     */
    @SaCheckPermission("models:students:list")
    @GetMapping("/status-list")
    @Log(title = "学生状态列表查询", businessType = BusinessType.OTHER)
    public TableDataInfo<StudentStatusVo> listStudentStatus(StudentStatusBo bo, PageQuery pageQuery) {
        return studentsService.queryStudentStatusList(bo, pageQuery);
    }

    /**
     * 导出学生状态列表
     * 业务场景：导出包含状态信息的学生列表用于分析
     */
    @SaCheckPermission("models:students:export")
    @Log(title = "学生状态列表", businessType = BusinessType.EXPORT)
    @PostMapping("/export-status")
    public void exportStudentStatus(StudentStatusBo bo, HttpServletResponse response) {
        PageQuery exportPageQuery = new PageQuery(10000, 1);
        List<StudentStatusVo> list = studentsService.queryStudentStatusList(bo, exportPageQuery).getRows();
        ExcelUtil.exportExcel(list, "学生状态列表", StudentStatusVo.class, response);
    }

    /**
     * 导出待分配学生列表
     */
    @SaCheckPermission("models:students:export")
    @Log(title = "待分配学生", businessType = BusinessType.EXPORT)
    @PostMapping("/export-unassigned")
    public void exportUnassignedStudents(StudentStatusBo bo, HttpServletResponse response) {
        PageQuery exportPageQuery = new PageQuery(10000, 1);
        List<StudentStatusVo> list = studentsService.queryUnassignedStudentList(bo, exportPageQuery).getRows();
        ExcelUtil.exportExcel(list, "待分配学生列表", StudentStatusVo.class, response);
    }

    /**
     * 导出已退学学生列表
     */
    @SaCheckPermission("models:students:export")
    @Log(title = "已退学学生", businessType = BusinessType.EXPORT)
    @PostMapping("/export-withdrawn")
    public void exportWithdrawnStudents(StudentStatusBo bo, HttpServletResponse response) {
        PageQuery exportPageQuery = new PageQuery(10000, 1);
        List<StudentStatusVo> list = studentsService.queryWithdrawnStudentList(bo, exportPageQuery).getRows();
        ExcelUtil.exportExcel(list, "已退学学生列表", StudentStatusVo.class, response);
    }

    // ========== 批量导入功能 ==========

    /**
     * 下载学生档案导入模板
     * 业务场景：提供标准的Excel模板供用户下载，确保导入数据格式正确
     */
    @PostMapping("/importTemplate")
    public void importTemplate(HttpServletResponse response) {
        ExcelUtil.exportExcel(List.of(), "学生档案数据", StudentsBo.class, response);
    }

    /**
     * 批量导入学生档案
     * 业务场景：从Excel文件批量导入学生档案，支持新增和更新模式
     *
     * @param file 上传的Excel文件
     * @param updateSupport 是否支持更新已存在的记录
     * @return 导入结果信息
     * @throws Exception 导入异常
     */
    @Log(title = "学生档案", businessType = BusinessType.IMPORT)
    @SaCheckPermission("models:students:import")
    @PostMapping("/importData")
    public R<Void> importData(MultipartFile file, boolean updateSupport) throws Exception {
        // 🔧 修复：使用StudentsImportVo而不是StudentsBo，避免列映射错误
        List<StudentsImportVo> studentsList = ExcelUtil.importExcel(file.getInputStream(), StudentsImportVo.class);
        String operName = LoginHelper.getUsername();
        String message = studentsService.importStudentsFromVo(studentsList, updateSupport, operName);
        return R.ok(message);
    }
}

