/**
 * 学生档案Service业务层处理
 * 🔧 循环依赖修复：使用@Lazy注解延迟加载RoleAutoAssignmentService
 *
 * @author Akari
 * @date 2025-06-13
 */
@Slf4j
@Service
public class StudentsServiceImpl implements IStudentsService {

    private final StudentsMapper baseMapper;

    // 🏗️ 重构后的专门服务类
    private final StudentsUserIntegrationService userIntegrationService;
    private final StudentsRoleAssignmentService roleAssignmentService;
    private final StudentsImportExportService importExportService;
    private final StudentsValidationService validationService;
    private final StudentsQueryService queryService;
    private final StudentsEventService eventService;
    private final StudentsBasicService basicService;

    /**
     * 构造函数
     */
    public StudentsServiceImpl(
            StudentsMapper baseMapper,
            StudentsUserIntegrationService userIntegrationService,
            StudentsRoleAssignmentService roleAssignmentService,
            StudentsImportExportService importExportService,
            StudentsValidationService validationService,
            StudentsQueryService queryService,
            StudentsEventService eventService,
            StudentsBasicService basicService) {
        this.baseMapper = baseMapper;
        this.userIntegrationService = userIntegrationService;
        this.roleAssignmentService = roleAssignmentService;
        this.importExportService = importExportService;
        this.validationService = validationService;
        this.queryService = queryService;
        this.eventService = eventService;
        this.basicService = basicService;
    }

    /**
     * 查询学生档案（包含用户信息）
     *
     * @param id 主键
     * @return 学生档案
     */
    @Override
    public StudentsVo queryById(Long id){
        return basicService.queryById(id);
    }

    /**
     * 分页查询学生档案列表（包含用户信息）
     *
     * @param bo        查询条件
     * @param pageQuery 分页参数
     * @return 学生档案分页列表
     */
    @Override
    public TableDataInfo<StudentsVo> queryPageList(StudentsBo bo, PageQuery pageQuery) {
        log.info("查询学生档案列表，条件：{}", bo);
        return basicService.queryPageList(bo, pageQuery);

    }

    /**
     * 查询符合条件的学生档案列表
     *
     * @param bo 查询条件
     * @return 学生档案列表
     */
    @Override
    public List<StudentsVo> queryList(StudentsBo bo) {
        return basicService.queryList(bo);
    }

    /**
     * 新增学生档案（同时创建系统用户）
     */
    @Override
    @CacheEvict(cacheNames = {
        CacheNames.STUDENTS_INFO,
        CacheNames.STUDENTS_LIST,
        CacheNames.STUDENT_STATUS,
        CacheNames.CLASS_STATS
    }, allEntries = true)
    // TODO: 临时注释敏感数据保护注解，待ruoyi-common-sensitive-protection模块修复后恢复
    /*
    @org.dromara.common.sensitive.protection.annotation.SensitiveDataProtection(
        types = {
            org.dromara.common.sensitive.protection.enums.SensitiveType.PHONE_NUMBER,
            org.dromara.common.sensitive.protection.enums.SensitiveType.REAL_NAME
        },
        strategy = org.dromara.common.sensitive.protection.enums.InterceptionStrategy.HASH_AND_BLOCK,
        complianceNote = "学员档案Service层新增-个人信息保护法合规",
        businessScene = "student_service_insert",
        retentionDays = 30
    )
    */
    @Transactional(rollbackFor = Exception.class)
    public Boolean insertByBo(StudentsBo bo) {
        log.info("新增学生档案，数据：{}", bo);

        try {
            // 🏗️ 重构：使用门面模式，委托给专门的服务处理
            if (bo.getUserId() != null) {
                return insertWithExistingUser(bo);
            } else {
                return insertWithNewUser(bo);
            }
        } catch (Exception e) {
            log.error("❌ 新增学生档案失败，错误：{}", e.getMessage(), e);
            throw new ServiceException("新增学生档案失败：" + e.getMessage());
        }
    }

    /**
     * 使用现有用户ID创建学生档案
     * 适用于已有系统用户账号的学生注册场景
     *
     * @param bo 学生业务对象，必须包含有效的userId
     * @return 是否创建成功
     */
    private Boolean insertWithExistingUser(StudentsBo bo) {
        log.info("📝 使用现有用户ID创建学生档案，用户ID：{}", bo.getUserId());

        Students studentEntity = MapstructUtils.convert(bo, Students.class);
        validationService.validEntityBeforeSaveWithOptimisticLock(studentEntity);
        boolean result = baseMapper.insert(studentEntity) > 0;

        if (result) {
            bo.setId(studentEntity.getId());
            log.info("✅ 学生档案创建成功（使用现有用户ID），学生ID：{}，用户ID：{}",
                    studentEntity.getId(), studentEntity.getUserId());
        }

        return result;
    }

    /**
     * 创建新用户并关联学生档案
     */
